INSERT INTO `flipcards` (`id`, `user_id`, `course_id`, `image`, `question`, `answer`, `hints1`, `hints2`, `hints3`, `hints4`, `user_answer`, `time`, `created_at`, `updated_at`) VALUES
(3, NULL, 15, NULL, 'ITIL 4', 'ITIL4 wants to introduce participants to the management of modern IT enable services, provide them with an understanding of the common language and key concepts and show them how they can improve their work and work of their organization with ITIL4 guidance.', 'introduce participants', 'IT enable services', 'Improve', NULL, NULL, NULL, '2021-12-01 22:12:56', NULL),
(6, NULL, 15, NULL, 'PURPOSE', 'The purpose of ITIL4 is to provide organization with comprehensive guidance for the management of IT enabled service in the digital economy', 'comprehensive guidance', 'IT enabled service', 'digital economy', NULL, NULL, NULL, '2021-12-01 22:21:37', NULL),
(7, NULL, 15, NULL, 'VALUE', 'Value is the perceived benefits, usefulness and importance of something.', 'benefits', 'usefulness', 'importance', NULL, NULL, NULL, '2021-12-01 22:32:51', NULL),
(9, NULL, 15, NULL, 'VALUE CREATION', 'An organization is a person or group of people that has its own functions with responsibilities, authorities and relationship to achieve its objectives.', 'own functions', 'responsibilities', 'relationship', NULL, NULL, NULL, '2021-12-01 22:42:33', NULL),
(10, NULL, 15, NULL, 'SERVICE', 'Service is a means of enabling value co-creation by facilitating outcomes that customers want to achieve, without the customer having to manage specific costs and risks.', 'facilitating outcomes', 'costs', 'risks', NULL, NULL, NULL, '2021-12-01 22:44:50', NULL),
(11, NULL, 15, NULL, 'SERVICE MANAGEMENT', 'Service management is defined as a specialized organizational capabilities for enabling value to customer in the form of service', 'specialized organizational', 'capabilities', 'value', NULL, NULL, NULL, '2021-12-01 22:47:54', NULL),
(12, NULL, 15, NULL, 'NEED FOR SERVICE MANAGEMENT', 'Technology is advancing faster today than ever before. Development such as cloud computing, infrastructure as a service, machine learning, and block chain, have opened fresh opportunities for value creation, and led to IT becoming an important business driver and source of competitive advantage .', 'cloud computing', 'machine learning', 'block chain', 'value creation', NULL, NULL, '2021-12-01 23:02:42', NULL),
(13, NULL, 15, NULL, 'SERVICE PROVIDER , CONSUMER & SERVICE RELATIONSHIP', 'Increasingly, organizations recognize that value is co-created through an active collaboration between providers and consumers, as well as other organizations that are part of the relevant service relationships', 'providers', 'consumers', NULL, NULL, NULL, NULL, '2021-12-01 23:50:54', NULL),
(15, NULL, 15, NULL, 'SERVICE & PRODUCT', 'Service is a means of enabling value co-creation by facilitating outcomes that customers want to achieve, without the customer having to manage specific costs and risks', 'value co-creation', 'achieve', 'costs', NULL, NULL, NULL, '2021-12-02 00:12:42', NULL),
(16, NULL, 15, NULL, 'SERVICE OFFERING', 'Service offering: A description of one or more services, designed to address the needs of a target consumer group. A service offering may include goods, access to resources, and service actions. ', 'goods', 'access to resources', 'service actions', NULL, NULL, NULL, '2021-12-02 00:14:40', NULL),
(18, NULL, 15, NULL, 'SERVICE RELATIONSHIP', 'Service relationship consist of joint activities performed by a service provider and a service consumer to ensure continual value co creation based on agreed and available service offering.', 'service consumer', 'ensure continual value', NULL, NULL, NULL, NULL, '2021-12-02 00:24:54', NULL),
(19, NULL, 15, NULL, 'UNDERSTANDING COST', 'Cost refer to the amount of money spent on a specific activity or resource.', 'activity', 'resource', NULL, NULL, NULL, NULL, '2021-12-02 00:26:53', NULL),
(20, NULL, 15, NULL, 'UNDERSTANDING RISK', 'A possible event that could cause harm or loss, or make it more difficult to achieve objectives.', 'harm or loss', 'achieve objectives', NULL, NULL, NULL, NULL, '2021-12-02 00:29:37', NULL),
(21, NULL, 15, NULL, 'SERIVCE VALUE SYSTEM', 'The ITIL SVS describes how all the components and activities of the organization work together as a system to enable value creation', 'activities of the organization', 'enable value creation', NULL, NULL, NULL, NULL, '2021-12-02 00:44:23', NULL),
(24, NULL, 15, NULL, 'VALUE STREAMS AND PROCESSES', 'Value stream: A series of steps an organization undertakes to create and deliver products and services to consumers.       Process is  set of interrelated or interacting activities that transform inputs into outputs.  A process takes one or more defined inputs and turns them into defined outputs.  Processes define the sequence of actions and their dependencies', 'create and deliver', 'is  set of interrelated or interacting', NULL, NULL, NULL, NULL, '2021-12-02 00:49:05', NULL),
(25, NULL, 15, NULL, 'INPUT OF THE SVS', 'Opportunities represent options or possibilities to add value for stakeholders or otherwise improve the organization. Demand is the need or desire for products and services among internal and external consumers. ', 'options or possibilities', 'add value', 'need or desire', NULL, NULL, NULL, '2021-12-02 00:53:14', NULL),
(26, NULL, 15, NULL, 'OUTCOME OF THE SVS', 'The  outcome  of  the  SVS  is  value,  that  is,  the  perceived  benefits,  usefulness  and importance of something. The ITIL SVS can enable the creation of many different types of value for a wide group of stakeholders', 'perceived  benefits', 'usefulness', 'importance of something', NULL, NULL, NULL, '2021-12-02 00:55:04', NULL),
(27, NULL, 15, NULL, 'GUIDING PRINCEPLES', 'Guiding  principles ae recommendations  that  can  guide  an  organization  in  all circumstances, regardless of changes in its goals, strategies, type of work, or management structure', 'goals', 'strategies', 'type of work', NULL, NULL, NULL, '2021-12-02 00:56:16', NULL),
(28, NULL, 15, NULL, 'GOVERNANCE', 'Governance The means by which an organization is directed and controlled', 'directed', 'controlled', NULL, NULL, NULL, NULL, '2021-12-02 00:59:08', NULL),
(29, NULL, 15, NULL, 'THE SERVICE VALUE CHAIN', 'Service  value  chain  A  set  of  interconnected  activities  that  an  organization performs to deliver a valuable product or service to its consumers and to facilitate value realization', 'interconnected  activities', 'deliver', 'facilitate', NULL, NULL, NULL, '2021-12-02 01:00:43', NULL),
(30, NULL, 15, NULL, 'COMPONENTS OF THE SVS: PRACTICES', 'Practices  sets  of  organizational  resources  designed  for  performing  work  or accomplishing an objective.', 'performing  work', 'accomplishing an objective', NULL, NULL, NULL, NULL, '2021-12-02 01:02:54', NULL),
(31, NULL, 15, NULL, 'CONTINUAL IMPROVEMENT', 'Continual improvement is a recurring organizational activity performed at all levels to ensure that an organization’s performance continually meets stakeholders’ expectations.', 'organizational activity', 'organization’s performance', NULL, NULL, NULL, NULL, '2021-12-02 01:04:44', NULL),
(32, NULL, 15, NULL, 'THE ITIL GUIDING PRINCIPLES', 'The guiding principles can be used to adopt a service management approach and adapt ITIL guidance to their own specific needs and circumstances.  The guiding principles allow organizations to effectively integrate the use of multiple methods into an overall approach to service management.  They are universally applicable to nearly any initiative and to relationships with all stakeholder groups.', 'to adopt', 'effectively integrate', 'any initiative', NULL, NULL, NULL, '2021-12-02 01:05:54', NULL),
(34, NULL, 15, NULL, 'COLLABORATE AND PROMOTE VISIBILITY', 'When initiatives involve the right people in the correct roles, efforts benefit from better buy-in, more relevance (because better information is available for decision-making) and increased likelihood of long-term success.', 'Information sharing', 'Understanding', 'Trust', 'Real Collaboration', NULL, NULL, '2021-12-02 01:12:14', NULL),
(35, NULL, 15, NULL, 'THINK AND WORK HOLISTICALLY', 'A holistic approach to service management requires an understanding of how all the parts of an organization work together in an integrated way', 'organization work', 'integrated way', NULL, NULL, NULL, NULL, '2021-12-02 01:13:35', NULL),
(36, NULL, 15, NULL, 'OPTIMIZE AND AUTOMATE', 'Optimization means to make something as effective and useful as it needs to be. Before an activity can be effectively automated, it should be standardize and optimized to whatever degree is possible and reasonable.', 'standardize', 'optimized', NULL, NULL, NULL, NULL, '2021-12-02 01:14:23', NULL),
(37, NULL, 15, NULL, 'SERVICE VALUE CHAIN', 'The central element of the SVS is the service value chain, an operating model which outlines the key activities required to respond to demand and facilitate value creation through the creation and management of product and service.', 'Engage', 'Design and transition', 'Deliver and support', 'Product and services', NULL, NULL, '2021-12-02 01:15:32', NULL),
(38, NULL, 15, NULL, 'ITIL PRACTICE', 'A Practice is a set of organizational resources designed for performing work or accomplishing an objective', 'multiple service', 'resources based', NULL, NULL, NULL, NULL, '2021-12-02 01:16:13', NULL),
(39, NULL, 15, NULL, 'GENERAL MANAGEMENT PRACTICE', 'General management practice have been adopted /adapted for service management from general business management domain', 'adopted', 'domain', NULL, NULL, NULL, NULL, '2021-12-02 01:16:49', NULL),
(40, NULL, 15, NULL, 'CONTINUAL IMPROVEMENT', 'The purpose of the continual improvement practice is to align the organization’s practice and service with changing business need through the ongoing identification and improvement of service, service components, practices or any element involved in the efficient and effective management of products and services.', 'across the organization', 'logging', 'business cases', NULL, NULL, NULL, '2021-12-02 01:17:50', NULL),
(41, NULL, 15, NULL, 'RELATIONSHIP MANAGEMENT', 'The purpose of the relationship management practice is to establish and nurture the links between the organization and its stakeholders at strategic and tactical levels.', 'Identification', 'Analysis', 'Monitoring', NULL, NULL, NULL, '2021-12-02 01:18:31', NULL),
(42, NULL, 15, NULL, 'INFORMATION SECURITY MANAGEMENT', 'The purpose of the information security management practice is to protect the information needed by the organization to conduct its business.', 'Confidentially', 'Integrity', 'Availability', NULL, NULL, NULL, '2021-12-02 01:19:13', NULL),
(43, NULL, 15, NULL, 'SUPPLIER MANAGEMENT', 'The purpose of the supplier management practice is to ensure that the organization’s suppliers and their performance are managed appropriately to support the provision of seamless, quality products and services. This can include creating closer, more collaborative relationships with key suppliers to uncover and realize new value and reduce risk of failure', 'ensure consistency', 'supplier strategy', 'Managing relationships', NULL, NULL, NULL, '2021-12-02 01:20:09', NULL),
(44, NULL, 15, NULL, 'SERVICE MANAGEMENT PRACTICE', 'Service management practices have been developed in service management and ITSM Industry SERVICE DESK', 'Availability management', 'Business analysis', 'Capacity and performance management', NULL, NULL, NULL, '2021-12-02 01:21:10', NULL),
(45, NULL, 15, NULL, 'SERVICE DESK', 'The purpose of the service desk practice is to capture demand for incident resolution and service requests. It should also be the point of communication for the service provider with all of its users.', 'Acknowledge', 'Classify', 'Own', 'Act', NULL, NULL, '2021-12-02 01:22:14', NULL),
(46, NULL, 15, NULL, 'SERVICE REQUEST', 'The purpose of the service request management practice is to support the agreed quality of a service by handling all pre-defined, user-initiated service requests in an effective and user-friendly manner.', 'pre-defined', 'user-friendly manner', NULL, NULL, NULL, NULL, '2021-12-02 01:23:01', NULL),
(47, NULL, 15, NULL, 'INCIDENT MANAGEMENT', 'The purpose of incident management is to minimize the negative impact of incidents by restoring normal service operation as quickly as possible.', 'logged', 'prioritized', 'resolution times', NULL, NULL, NULL, '2021-12-02 01:23:49', NULL),
(48, NULL, 15, NULL, 'PROBLEM MANAGEMENT', 'The purpose of problem management is to reduce the likelihood and impact of incidents by identifying actual and potential causes of incidents, and managing workarounds and known errors.', 'actual', 'potential causes', 'workarounds', 'known errors', NULL, NULL, '2021-12-02 01:24:49', NULL),
(49, NULL, 15, NULL, 'CHANGE ENABLEMENT', 'The purpose of the change control practice is to maximize the number of successful IT changes  by  ensuring  that  risks  have  been  properly  assessed,  authorizing  changes  to proceed, and managing a change schedule', 'risks', 'assessed', 'schedule', NULL, NULL, NULL, '2021-12-02 01:25:35', NULL),
(50, NULL, 15, NULL, 'CHANGE CONTROL', 'When the procedure for a standard change is created or modified there should be a full risk assessment and authorization as for any other change.', 'standard', 'normal', 'emergency', NULL, NULL, NULL, '2021-12-02 01:26:13', NULL),
(51, NULL, 15, NULL, 'MONITORING AND EVENT MANAGEMENT', 'The  purpose  of  the  monitoring  and  event  management  practice  is  to  systematically observe services and service components, and record and report selected changes of state identified  as  events.', 'record', 'observe', 'components', NULL, NULL, NULL, '2021-12-02 01:39:10', NULL),
(52, NULL, 15, NULL, 'IT ASSET MANAGEMENT', 'The purpose of the IT asset management practice is to plan and manage the full lifecycle of all IT assets, to help the organization', 'plan', 'manage', 'help', NULL, NULL, NULL, '2021-12-02 01:39:49', NULL),
(53, NULL, 15, NULL, 'SERVICE CONFIGURATION MANAGEMENT', 'The purpose of the service configuration management practice is to ensure that accurate and reliable information about the configuration of services, and the CIs that support them, is available when and where it is needed.', 'accurate', 'reliable', 'configuration', NULL, NULL, NULL, '2021-12-02 01:40:34', NULL),
(54, NULL, 15, NULL, 'AVAILABILITY MANAGEMENT', 'The purpose of the availability management practice is to ensure that services deliver agreed levels of availability to meet the needs of customers and users.', 'deliver', 'customers', 'users', NULL, NULL, NULL, '2021-12-02 01:41:20', NULL),
(55, NULL, 15, NULL, 'CAPACITY MANAGEMENT', 'The purpose of the capacity and performance management practice is to ensure that services achieve agreed and expected performance, satisfying current and future demand in a cost-effective way', 'achieve', 'performance', 'demand', NULL, NULL, NULL, '2021-12-02 01:42:04', NULL),
(56, NULL, 15, NULL, 'SERVICE LEVEL MANAGEMENT', 'The purpose of the service level management practice is to set clear business-based targets for service performance, so that the delivery of a service can be properly assessed, monitored and managed against these targets.', 'service performance', 'delivery', 'managed', NULL, NULL, NULL, '2021-12-02 01:43:06', NULL),
(57, NULL, 15, NULL, 'RELEASE MANAGEMENT', 'The purpose of the release management practice is to make new and changed services and features available for use', 'services', 'features', NULL, NULL, NULL, NULL, '2021-12-02 01:43:37', NULL),
(58, NULL, 15, NULL, 'DEPLOYMENT MANAGEMENT', 'The  purpose  of  the  deployment  management  practice  is  to  move  new  or  changed hardware, software, documentation, processes, or any other component to live environments.  It may also be involved in deploying components to other environments for testing or staging.', 'hardware', 'software', 'processes', NULL, NULL, NULL, '2021-12-02 01:44:19', NULL),
(59, NULL, 15, NULL, 'SERVICE CONTINUITY MANAGEMENT', 'The  purpose  of  the  service  continuity  management  practice  is  to  ensure  that  the availability and performance of a service is maintained at a sufficient level in the event of a disaster. The practice provides a framework for building organizational resilience with the capability of producing an effective response that safeguards the interests of key stakeholders, and the organization’s reputation, brand and value-creating activities', 'disaster', 'resilience', 'stakeholders', NULL, NULL, NULL, '2021-12-02 01:45:19', NULL),
(60, NULL, 15, NULL, 'SERVICE VALUE CHAIN: PLAN', 'The purpose of this value chain activity is to ensure a shared understanding of the vision, current status and improvement direction for all four dimensions and all products and services across the organization.', 'current status', 'improvement', 'products', 'services', NULL, NULL, '2021-12-02 02:24:23', NULL),
(61, NULL, 15, NULL, 'SERVICE VALUE CHAIN : IMPROVE', 'The purpose of this value chain activity is to ensure continual improvement of products, services and practices across all value chain activities and the four dimensions of service management.', 'products', 'practices', 'ensure', NULL, NULL, NULL, '2021-12-02 02:26:21', NULL),
(62, NULL, 15, NULL, 'SERVICE VALUE CHAIN : ENGAGE', 'The purpose of this value chain activity is to provide a good understanding of stakeholder needs, transparency, and continual engagement and good relationships with all stakeholders.', 'transparency', 'good relationships', NULL, NULL, NULL, NULL, '2021-12-02 02:27:17', NULL),
(63, NULL, 15, NULL, 'SERVICE VALUE CHAIN : DESIGN & TRANSITION', 'The purpose of this value chain activity is to ensure that products and services continually meet stakeholder expectations for quality, costs and time-to-market', 'for quality', 'cost', 'time to market', NULL, NULL, NULL, '2021-12-02 02:28:13', NULL),
(64, NULL, 15, NULL, 'SERVICE VALUE CHAIN : OBTAIN/BUILD', 'The purpose of this value chain activity is to ensure that service components are available when and where they are needed, and meet agreed specifications', 'components are available', 'agreed specifications', NULL, NULL, NULL, NULL, '2021-12-02 02:29:16', NULL),
(65, NULL, 15, NULL, 'SERVICE VALUE CHAIN : DELIVER & SUPPORT', 'The purpose of this value chain activity is to ensure that services are delivered and supported according to agreed specifications and stakeholders’ expectations.', 'delivered and supported', 'agreed specifications', 'expectations', NULL, NULL, NULL, '2021-12-02 02:30:10', NULL);
